document.querySelector(".weather"),document.querySelector("#search"),document.querySelector("form").addEventListener("submit",(function(e){e.preventDefault(),async function(e){let t=e.value;url="https://api.openweathermap.org/data/2.5/weather?q="+t+"&units=metric&appid=8235a17d6d4b4b4ea5ba07566b7ef6d7";try{const t=await fetch(url,{mode:"cors"}),n=await t.json();if(console.log(n),200!=n.cod)return void alert("Enter valid city name");const r=document.querySelector(".name");r.innerText="",r.innerText=n.name;const o=document.querySelector(".temp");console.log(o),o.innerText="",o.innerText=n.main.temp+String.fromCharCode(176)+"C";const i=document.querySelector(".weather-info");i.innerText="",i.innerText="Info: "+n.weather[0].main;const c=document.querySelector(".description");c.innerText="",c.innerText="Description: "+n.weather[0].description;const u=document.querySelector(".feels-like");u.innerText="",u.innerText="Feels Like: "+n.main.feels_like+String.fromCharCode(176)+"C";const a=document.querySelector(".humidity");a.innerText="",a.innerText="Humidity: "+n.main.humidity+" %";const d=document.querySelector(".wind-speed");d.innerText="",d.innerText="Wind Speed: "+n.wind.speed+" km/h";const m=document.querySelector(".pressure");m.innerText="",m.innerText="Pressure: "+n.main.pressure+" mb",e.value=""}catch(e){console.log(e)}}(document.getElementById("city"))}));