document.querySelector(".weather"),document.querySelector("#search"),document.querySelector("form").addEventListener("submit",(function(e){e.preventDefault(),async function(e){let n=e.value;url="api.openweathermap.org/data/2.5/weather?q="+n+"&units=metric&appid=8235a17d6d4b4b4ea5ba07566b7ef6d7";try{const n=await fetch(url,{mode:"cors"}),t=await n.json();if(console.log(t),200!=t.cod)return void alert("Enter valid city name");const r=document.querySelector(".name");r.innerText="",r.innerText=t.name;const o=document.querySelector(".temp");console.log(o),o.innerText="",o.innerText=t.main.temp+String.fromCharCode(176)+"C";const i=document.querySelector(".weather-info");i.innerText="",i.innerText="Info: "+t.weather[0].main;const c=document.querySelector(".description");c.innerText="",c.innerText="Description: "+t.weather[0].description;const u=document.querySelector(".feels-like");u.innerText="",u.innerText="Feels Like: "+t.main.feels_like+String.fromCharCode(176)+"C";const a=document.querySelector(".humidity");a.innerText="",a.innerText="Humidity: "+t.main.humidity+" %";const d=document.querySelector(".wind-speed");d.innerText="",d.innerText="Wind Speed: "+t.wind.speed+" km/h";const m=document.querySelector(".pressure");m.innerText="",m.innerText="Pressure: "+t.main.pressure+" mb",e.value=""}catch(e){console.log(e)}}(document.getElementById("city"))}));